import React from "react";
import { useState } from "react";
import {useDispatch} from "react-redux"
import { getProductName } from "../redux/actions";

import { SBdiv, SBinput, SBbutton, SBimg } from "./Styles/StyledSearchBar"

import './SearchBar.css';

export default function SearchBar (){
    const dispatch = useDispatch()
    const [name,setName] = useState("")

    function inputChangeHandler(e){
    e.preventDefault()
    setName(e.target.value)
    
    }

    function submitHandler(e){
        e.preventDefault()
        dispatch(getProductName(name))    
    }

    return (
        <SBdiv>
            <SBinput
             type="text"
             placeholder="Search Products by name..."
             onChange={(e) => inputChangeHandler(e)}

             />
            <SBbutton type="submit" onClick={(e)=> submitHandler(e)}>Search</SBbutton>
            <SBimg src={require('../images/searchBarIcon.png')} alt="Search Bar" width='2%' height='2%'/>
        </SBdiv>
    )
}
